INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('845d1ce67a62d376ec26c8ffbb22d492', '845d1ce67a62d376ec26c8ffbb22d492', 'de', 'Die Einstellungen des Nachrichtensystems bieten die Möglichkeit z.B. eine Weiterleitung der in Stud.IP empfangenen Nachrichten an die E-Mail-Adresse zu veranlassen.', 'dispatch.php/settings/messaging', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('85cbaa1648af330cc4420b57df4be29c', '85cbaa1648af330cc4420b57df4be29c', 'de', 'Die Einstellungen des Terminkalenders bieten die Möglichkeit, diesen an eigene Bedürfnisse anzupassen.', 'dispatch.php/settings/calendar', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1da144f3c6f52af0566c343151a6a6ff', '1da144f3c6f52af0566c343151a6a6ff', 'de', 'In den Benachrichtigungseinstellungen kann ausgewählt werden, bei welchen Änderungen innerhalb einer Veranstaltung eine Benachrichtigung erfolgen soll.', 'dispatch.php/settings/notification', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('01ad8998268101ad186babf43dac30a4', '01ad8998268101ad186babf43dac30a4', 'de', 'In den Standard-Vertretungseinstellungen können Dozierende eine Standard-Vertretung festlegen, die alle Veranstaltungen des Dozierenden verwalten und ändern kann.', 'dispatch.php/settings/deputies', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1c61657979ce22a9af023248a617f6b2', '1c61657979ce22a9af023248a617f6b2', 'de', 'Die Startseite wird nach dem Einloggen angezeigt und kann an persönliche Bedürfnisse mit Hilfe von Widgets angepasst werden.', 'dispatch.php/start', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('74c1da86f33f5adfb43e10220bfad238', '74c1da86f33f5adfb43e10220bfad238', 'de', 'Die Veranstaltungsseite zeigt alle abonnierten Veranstaltungen (standardmäßig nur die der letzten beiden Semester), alle abonnierten Studiengruppen sowie alle Einrichtungen, denen man zugeordnet wurde. Die Anzeige lässt sich über Farbgruppierungen, Semesterfilter usw. anpassen.', 'dispatch.php/my_courses', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('437c83a27473ef8139b47198101067fb', '437c83a27473ef8139b47198101067fb', 'de', 'Hier erscheinen archivierte Veranstaltungen, denen der Nutzer zugeordnet ist. Inhalte können nicht mehr verändert, jedoch hinterlegte Dateien als zip-Datei heruntergeladen werden.', 'dispatch.php/my_courses/archive', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('04457f9a66eab07618fe502d470a9711', '04457f9a66eab07618fe502d470a9711', 'de', 'In der Übersicht finden sich veranstaltungsbezogene Kurz- und Detail-Informationen, Ankündigungen, Termine und Umfragen.', 'dispatch.php/course/overview', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1d1323471cf21637f51284f4e6f2d135', '1d1323471cf21637f51284f4e6f2d135', 'de', 'Detaillierte Informationen über die Veranstaltung werden angezeigt, wie z.B. die Veranstaltungsnummer, Zuordnungen, DozentInnen, TutorInnen etc. In den Detail-Informationen ist unter Aktionen das Eintragen in eine Veranstaltung möglich.', 'dispatch.php/course/details', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('7f4a1f5e3dfe2a459cf0eb357667d91c', '7f4a1f5e3dfe2a459cf0eb357667d91c', 'de', 'Mit den Verwaltungsfunktionen lassen sich die Eigenschaften der Veranstaltung nachträglich ändern. Unter Aktionen ist die Simulation der Studierendenansicht möglich.', 'dispatch.php/course/management', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('4698cafeb9823735c50fd3a1745950ba', '4698cafeb9823735c50fd3a1745950ba', 'de', 'In den Grunddaten können Titel, Beschreibung, Dozierende etc. geändert werden. Die Bearbeitung kann teilweise gesperrt sein, wenn Daten aus anderen Systemen (z.B. LSF/ UniVZ) übernommen werden.', 'dispatch.php/course/basicdata/view', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('5fab81bbd1e19949f304df08ea21ca1b', '5fab81bbd1e19949f304df08ea21ca1b', 'de', 'Mit der Bild-Hochladen-Funktion lässt sich das Bild der Veranstaltung ändern, was Studierenden bei der Unterscheidung von Veranstaltungen auf der Meine-Veranstaltungen-Seite helfen kann.', 'dispatch.php/course/avatar/update', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('19c2bc232075602bd39efd4b6623d576', '19c2bc232075602bd39efd4b6623d576', 'de', 'Mit der Studienbereiche-Funktion kann die Veranstaltung einem Studienbereich zugeordnet werden. Die Bearbeitung kann gesperrt sein, wenn Daten aus anderen Systemen (z.B. LSF/ UniVZ) übernommen werden.', 'dispatch.php/course/study_areas/show', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('70274c459a69e34bbf520e690a8e472b', '70274c459a69e34bbf520e690a8e472b', 'de', 'Mit der Zeiten/Räume-Funktion können die Semester-, Termin- und Raumangaben der Veranstaltung geändert werden. Die Bearbeitung kann gesperrt sein, wenn Daten aus anderen Systemen (z.B. LSF/ UniVZ) übernommen werden.', 'raumzeit.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('19d47b782ac5c8b8b21bd1f94858a0fa', '19d47b782ac5c8b8b21bd1f94858a0fa', 'de', 'Mit Zugangsberechtigungen (Anmeldeverfahren) lässt sich z.B. durch Passwörter, Zeitsteuerung und TeilnehmerInnenbeschränkung der Zugang zu einer Veranstaltung regulieren.', 'dispatch.php/course/admission', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('e939ac70210674f49a36ac428167a9b8', 'e939ac70210674f49a36ac428167a9b8', 'de', 'Mit der Umfragen-und-Tests-Funktion lassen sich (zeitgesteuerte) Umfragen oder einzelne Multiple-/Single-Choice-Fragen für Veranstaltungen, Studiengruppen oder das Profil erstellen.', 'admin_vote.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('2689cecba24e021f05fcece5e4c96057', '2689cecba24e021f05fcece5e4c96057', 'de', 'Mit der Evaluationen-Funktion lassen sich Befragungen mit Multiple-Choice, Likert- und Freitextfragen für Veranstaltungen, Studiengruppen, das eigene Profil oder Einrichtungen erstellen. Dabei können auch öffentliche Vorlagen anderer Personen verwendet werden. Es werden alle zukünftigen, laufenden und beendeten Evaluationen angezeigt.', 'admin_evaluation.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('194874212676ced8d45e1883da1ad456', '194874212676ced8d45e1883da1ad456', 'de', 'Das Forum ist eine textbasierte, zeit- und ortsunabhängige Möglichkeit zum Austausch von Fragen, Meinungen und Erfahrungen. Beiträge können abonniert, exportiert, als Favoriten gekennzeichnet und editiert werden. Über die Navigation links können unterschieldiche Ansichten (z.B. Neue Beiträge seit letztem LogIn) gewählt werden.', 'plugins.php/coreforum', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('a20036992a06e97a984832626121d99a', 'a20036992a06e97a984832626121d99a', 'de', 'Die TeilnehmerInnenliste zeigt eine Liste der Teilnehmenden dieser Veranstaltung. Weitere Teilnehmende können von Dozierenden hinzugefügt, entfernt, herabgestuft, heraufgestuft oder selbstdefinierten Gruppen zugeordnet werden.', 'dispatch.php/course/members', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('d79ca3bc4a8251862339b1c934504a54', 'd79ca3bc4a8251862339b1c934504a54', 'de', 'Hier werden die selbstdefinierten Gruppen angezeigt. An diese können Nachrichten versendet werden. Ein Klick auf die orangenen Pfeile vor dem Gruppenname ordnet Sie der Gruppe zu.', 'statusgruppen.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('e22701c71b4425fb5a95adf725866097', 'e22701c71b4425fb5a95adf725866097', 'de', 'Hier können Gruppen erstellt und verwaltet werden. Wenn der Selbsteintrag aktiviert ist, können sich TeilnehmerInnen selbst ein- und austragen.', 'admin_statusgruppe.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('aa77d5ee6e0f9a9e6f4a1bbabeaf4a7e', 'aa77d5ee6e0f9a9e6f4a1bbabeaf4a7e', 'de', 'Die Anwesenheitsliste zeigt alle Sitzungstermine (Sitzung, Vorlesung, Übung, Praktikum) des Ablaufplans und ermöglicht das Eintragen von Studierenden durch die Dozierenden in Stud.IP sowie einen Export der Liste zur Übersicht oder als Grundlage handschriftlicher Eintragungen.', 'participantsattendanceplugin/show', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('ac7326260fd5ca4fa83c1154f2ffc7b9', 'ac7326260fd5ca4fa83c1154f2ffc7b9', 'de', 'Die Dateiverwaltung bietet die Möglichkeit zum Hochladen, Verlinken, Verwalten und Herunterladen von Dateien. ', 'folder.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('29c3bfa01ddbaaa998094d3ee975a06a', '29c3bfa01ddbaaa998094d3ee975a06a', 'de', 'Der Ablaufplan zeigt Termine, Themen und Räume der Veranstaltung an. Einzelne Termine können bearbeitet werden, z.B. können Themen zu Terminen hinzugefügt werden.', 'dispatch.php/course/dates', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('c4dee277f741cfa7d5a65fa0c6bead4c', 'c4dee277f741cfa7d5a65fa0c6bead4c', 'de', 'Hier können Termine mit Themen versehen werden oder bereits eingegebene Themen übernommen und bearbeitet werden.', 'dispatch.php/course/topics', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('c01725d6a3da568e1b07aee4e68a7e1f', 'c01725d6a3da568e1b07aee4e68a7e1f', 'de', 'Diese Seite ermöglicht das Hinterlegen von freien Informationen, Links etc.', 'dispatch.php/course/scm', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('be204bdd0fce91702f51597bf8428fba', 'be204bdd0fce91702f51597bf8428fba', 'de', 'Das Wiki ermöglicht ein gemeinsames, asynchrones Erstellen und Bearbeiten von Texten. Texte lassen sich formatieren und miteinander verknüpfen, so dass ein verzweigtes Nachschlagewerk entsteht. ', 'wiki.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('707b0db0e45fc3bab04be7eff38c1d32', '707b0db0e45fc3bab04be7eff38c1d32', 'de', 'Die Literaturseite bietet Lehrenden die Möglichkeit, Literaturlisten zu erstellen oder aus Literaturverwaltungsprogrammen zu importieren. Diese Listen können in Lehrveranstaltungen kopiert und sichtbar geschaltet werden. Je nach Anbindung kann im tatsächlichen Buchbestand der Hochschule recherchiert werden. ', 'dispatch.php/course/literature', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('8dd3b80d9f95218d67edc3cb570559ff', '8dd3b80d9f95218d67edc3cb570559ff', 'de', 'Hier lassen sich Literaturlisten bearbeiten und in der Veranstaltung sichtbar schalten (mit Klick auf das "Auge").', 'dispatch.php/literature/edit_list', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('e29098d188ae25c298d78978de50bf09', 'e29098d188ae25c298d78978de50bf09', 'de', 'Hier kann in Katalogen nach Literatur gesucht und diese zur Merkliste hinzugefügt werden.', 'dispatch.php/literature/search', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('a1ea37130799a59f7774473f1a681141', 'a1ea37130799a59f7774473f1a681141', 'de', 'Die Lernmodulschnittstelle ermöglicht es, Selbstlerneinheiten oder Tests aus externen Programmen wie ILIAS und LON-CAPA in Stud.IP zur Verfügung zu stellen.', 'dispatch.php/course/elearning/show', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('595c46d86f681f7da4bd2fae780db618', '595c46d86f681f7da4bd2fae780db618', 'de', 'Wählen Sie das gewünschte System und anschließend das Lernmodul/ den Test aus. Schreibrechte bestimmen, wer zukünftig das Lernmodul bearbeiten darf. In der Sidebar befindet sich die Option "Zuordnungen aktualisieren", um geänderte Inhalte z.B. im ILIAS Kurs zu Stud.IP zu übertragen.', 'dispatch.php/course/elearning/edit', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('6529fd70b461fa4a9242e874fbf2a5d3', '6529fd70b461fa4a9242e874fbf2a5d3', 'de', 'In DoIT! haben Lehrende die Möglichkeit, verschiedene Arten von Aufgaben zu stellen, inklusive Hochladen von Dateien, Multiple-Choice-Fragen und Peer Reviewing. Die Aufgabenbearbeitung kann zeitlich befristet werden und wahlweise in Gruppen erfolgen.', 'plugins.php/reloadedplugin/show', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('95ff3a2a68dae73bcb14a4a538a8e4b5', '95ff3a2a68dae73bcb14a4a538a8e4b5', 'de', 'Blubbern ist eine Mischform aus Forum und Chat, bei dem Beiträge der Teilnehmenden in Echtzeit angezeigt werden. Andere können über einen Beitrag informiert werden, indem sie per @benutzername oder @"Vorname Nachname" im Beitrag erwähnt werden.', 'plugins.php/blubber/streams/forum', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('633dab120ce3969c42f33aeb3a59fcc1', '633dab120ce3969c42f33aeb3a59fcc1', 'de', 'Der Gruppenkalender bietet eine Übersicht über Veranstaltungstermine und personalisierte Zusatztermine für diese Veranstaltung. ', 'plugins.php/gruppenkalenderplugin/show', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1058f03da5b6fc6a5ff3a08c9c1fa5f7', '1058f03da5b6fc6a5ff3a08c9c1fa5f7', 'de', 'Hier können der Veranstaltung weitere Funktionen hinzugefügt werden.', 'dispatch.php/course/plus', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('8a1d7d04c70d93be44e8fe6a8e8c3443', '8a1d7d04c70d93be44e8fe6a8e8c3443', 'de', 'Das Lerntagebuch unterstützt den selbstgesteuerten Lernprozess der Studierenden und wird von ihnen selbstständig geführt. Anfragen zu Arbeitsschritten an die Dozierenden sind möglich, bestimmte Daten können individualisiert freigegeben werden.', 'plugins.php/lerntagebuchplugin/overview', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('72cec29d985f3e6d7df2b5fabb7fe666', '72cec29d985f3e6d7df2b5fabb7fe666', 'de', 'Konfiguation des Lerntagebuchs für Studierende und Anlegen eines Lerntagebuchs für die Dozierenden.', 'plugins.php/lerntagebuchplugin/admin_settings', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('2fcc672d91f2627ab5ca48499e8b1617', '2fcc672d91f2627ab5ca48499e8b1617', 'de', 'Möglichkeit zur Bereitstellung von Vorlesungsaufzeichnungen und Podcasts für Studierende der Veranstaltung (durch Verlinkung auf die Dateien auf dem Medienserver). ', 'plugins.php/mediacastsplugin/show', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('bd0770f9eef5c10fc211114ac35fbe9b', 'bd0770f9eef5c10fc211114ac35fbe9b', 'de', 'Diese Seite zeigt die Studiengruppen an, denen die/der NutzerIn zugeordnet ist. Studiengruppen sind eine einfache Möglichkeit, mit Mitstudierenden, KollegInnen und anderen zusammenzuarbeiten. Jede/r NutzerIn kann Studiengruppen anlegen oder nach ihnen suchen. Die Farbgruppierung kann individuell angepasst werden.', 'dispatch.php/my_studygroups', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('82a17a5f19d211268b1fa90a1ebe0894', '82a17a5f19d211268b1fa90a1ebe0894', 'de', 'Hier kann eine neue Studiengruppe angelegt werden. Jede/r Stud.IP-NutzerIn kann Studiengruppen anlegen und nach eigenen Bedürfnissen konfigurieren.', 'dispatch.php/course/studygroup/new', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('e03cec310c0a884aee80c2d1eea3a53e', 'e03cec310c0a884aee80c2d1eea3a53e', 'de', 'Diese Seite zeigt alle Studiengruppen an, die in Stud.IP existieren. Studiengruppen sind eine einfache Möglichkeit, mit Mitstudierenden, KollegInnen und anderen zusammenzuarbeiten. Jede/r NutzerIn kann Studiengruppen anlegen oder nach ihnen suchen.', 'dispatch.php/studygroup/browse', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('f92b5422246f585f051de1a81602dd56', 'f92b5422246f585f051de1a81602dd56', 'de', 'Hier können Name, Funktionen und Zugangsbeschränkung der Studiengruppe bearbeitet werden.', 'dispatch.php/course/studygroup/edit', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1dca5b0b83f7bca92ec4add50d34b8c5', '1dca5b0b83f7bca92ec4add50d34b8c5', 'de', 'Hier können der Studiengruppe Mitglieder hinzugefügt und Nachrichten an diese versendet werden.', 'dispatch.php/course/studygroup/members', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1f6e2f98affbffb1d12904355e9313e5', '1f6e2f98affbffb1d12904355e9313e5', 'de', 'Diese Seite zeigt die Einrichtungen an, denen die/der NutzerIn zugeordnet ist.', 'dispatch.php/my_institutes', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('bf9eb8f2c3842865009342b89fd35476', 'bf9eb8f2c3842865009342b89fd35476', 'de', 'Die Nachrichtenseite bietet einen Überblick über erhaltene, systeminterne Nachrichten, welche mit selbstgewählten Schlüsselwörtern (sog. Tags) versehen werden können, um sie später leichter wieder auffinden zu können.', 'dispatch.php/messages/overview', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('6acc653cfabd3a0d4433ff0ab417bf6a', '6acc653cfabd3a0d4433ff0ab417bf6a', 'de', 'Übersicht über gesendete, systeminterne Nachrichten, welche mit selbstgewählten Schlüsselwörtern (sog. Tags) versehen werden können, um sie später leichter wieder auffinden zu können. ', 'dispatch.php/messages/sent', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('690e6eff3e83a5f372ec99fc49cafeb2', '690e6eff3e83a5f372ec99fc49cafeb2', 'de', 'Blubbern ist das Stud.IP Echtzeitforum, eine Mischform aus Forum und Chat. Andere können über einen Beitrag informiert werden, indem sie per @benutzername oder @"Vorname Nachname" im Beitrag erwähnt werden. Texte lassen sich formatieren und durch Smileys ergänzen.', 'plugins.php/blubber/streams/global', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('cd69b74cd46172785bf2147fb0582e3c', 'cd69b74cd46172785bf2147fb0582e3c', 'de', 'Hier kann ein benutzerdefinierter Blubber-Stream erstellt werden. Er besteht immer aus einer Sammlung von Beiträgen aus ausgewählten Veranstaltungen, Kontaktgruppen und Schlagwörten, die auf Basis einer Filterung noch weiter eingeschränkt werden können. Der neue benutzerdefinierte Stream findet sich nach dem Klick auf den Speichern-Button in der Navigation unter Globaler Stream.', 'plugins.php/blubber/streams/edit', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('394a45f94e1d84d3744027a5a69d9e3e', '394a45f94e1d84d3744027a5a69d9e3e', 'de', 'Auf dieser Seite lässt sich einsehen, welche Kontakte gerade online sind. Diesen Personen kann eine Nachricht geschickt werden. Das Klicken auf den Namen einer Person leitet zu deren Profil weiter.', 'dispatch.php/online', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('245ce01d7a0175ab0b977ae822821e9e', '245ce01d7a0175ab0b977ae822821e9e', 'de', 'Diese Seite bietet die Möglichkeit Stud.IP-Nutzende in das eigene Adressbuch einzutragen und alle bereits im Adressbuch befindlichen Kontakte aufzulisten.', 'contact.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('752d441cd321b05c55c8a5d9aa48ddce', '752d441cd321b05c55c8a5d9aa48ddce', 'de', 'Auf dieser Seite können Kontakte aus dem Adressbuch in selbstdefinierte Gruppen sortiert werden.', 'contact_statusgruppen.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('94a193baa212abbc9004280a1498e724', '94a193baa212abbc9004280a1498e724', 'de', 'Hier können Kontaktgruppen oder das gesamte Adressbuch exportiert werden, um sie in einem externen Programm importieren zu können.', 'contact_export.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('7ebdd278d06f9fc1d2659a54bb3171c1', '7ebdd278d06f9fc1d2659a54bb3171c1', 'de', 'Die Rangliste sortiert die Stud.IP-Nutzenden absteigend anhand ihrer Punktzahl. Die Punktzahl wächst mit den Aktivitäten in Stud.IP und repräsentiert so die Erfahrung der Nutzenden mit dem System. Indem das Kästchen links mit einem Haken versehen wird, wird der eigene Wert für andere NutzerInnen in der Rangliste sichtbar gemacht. In der Grundeinstellung ist der eigene Wert nicht öffentlich sichtbar.', 'dispatch.php/score', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('82537b14dd3714ec9636124ed5af3272', '82537b14dd3714ec9636124ed5af3272', 'de', 'Die Profilseite ermöglicht die Änderung der eigenen Nutzerdaten inkl. Profilbild und Kategorien. Ähnlich wie in Facebook können Kommentare hinterlassen werden. Das Profil von Lehrenden enthält Sprechstunden und Raumangaben. Daneben bietet die Seite die Verwaltung eigener Dateien.', 'dispatch.php/profile', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('ebb5bc1d831d460c06e3c6662236c159', 'ebb5bc1d831d460c06e3c6662236c159', 'de', 'Hier kann ein Profilbild hochgeladen werden.', 'dispatch.php/settings/avatar', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('25255dc15fd0d6260bc1abd1f10aecc5', '25255dc15fd0d6260bc1abd1f10aecc5', 'de', 'Individuelle Nutzerdaten, wie bspw. E-Mail-Adresse, können auf dieser Seite verändert und angepasst werden. ', 'dispatch.php/settings/account', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('d704267767d4c559aa9e552be60c49b5', 'd704267767d4c559aa9e552be60c49b5', 'de', 'Hier kann das Passwort für den Stud.IP-Account geändert werden.', 'dispatch.php/settings/password', '3.1', 0, 0, 1, '', '', 1406641688, 0);
