INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('ac5df1de9c75fc92af7718b2103d3037', 'ac5df1de9c75fc92af7718b2103d3037', 'de', 'Blubbern ist eine Mischform aus Forum und Chat. Nachrichten werden im öffentlichen Stream dargestellt. Andere Nutzer können über einen Beitrag informiert werden, indem sie per @benutzername oder @"Vorname Nachname" im Beitrag erwähnt werden.', 'plugins.php/blubber/streams/profile', '3.1', 0, 0, 1, '', '', 1406641688, 0);
