INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('164f77ab2cb7d38fd1ea20ed725834fd', '164f77ab2cb7d38fd1ea20ed725834fd', 'de', 'Hier findet sich eine Übersicht über die Termine, die von Studierenden gebucht wurden.', 'plugins.php/homepageterminvergabeplugin/show_bookings', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1289e991a93dce5a0b4edd678514325e', '1289e991a93dce5a0b4edd678514325e', 'de', 'Hier können einzelne Inhaltselemente nachträglich aktiviert oder deaktiviert werden. Aktivierte Inhaltselemente fügen neue Funktionen zu Ihrem Profil oder Ihren Einstellungen hinzu. Diese werden meist als neuer Reiter im Menü erscheinen. Wenn Funktionalitäten nicht benötigt werden, können diese hier deaktiviert werden. Die entsprechenden Menüpunkte werden dann ausgeblendet.', 'dispatch.php/profilemodules', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('b677e8b5f1bd7e8acbe474177449c4e1', 'b677e8b5f1bd7e8acbe474177449c4e1', 'de', 'Die Dateiverwaltung bietet die Möglichkeit zum Hochladen, Verwalten und Herunterladen persönlicher Dateien, die nicht für andere einsehbar sind. ', 'dispatch.php/document/files', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('35b1860b95854a2533b6ecfbbf04ab71', '35b1860b95854a2533b6ecfbbf04ab71', 'de', 'Der Stundenplan besteht aus abonnierten Veranstaltungen, die ein- und ausgeblendet sowie in Darstellungsgröße und -form angepasst werden können.', 'dispatch.php/calendar/schedule', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('db5a995bd12ba8e2ae96adcabeb8c8f7', 'db5a995bd12ba8e2ae96adcabeb8c8f7', 'de', 'Der Terminkalender besteht aus abonnierten Veranstaltungen und eigenen Terminen. Er kann bearbeitet, in der Anzeige verändert und mit externen Programmen (z.B. Outlook) abgeglichen werden. ', 'calendar.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('87489a40097e5c26f1d1349c072610de', '87489a40097e5c26f1d1349c072610de', 'de', 'Mit der Veranstaltungssuche können Veranstaltungen, Studiengruppen usw. in verschiedenen Semestern und nach verschiedenen Suchkriterien (siehe "Erweiterte Suche anzeigen"in der Sidebar) gefunden werden. Das aktuelle Semester ist vorgewählt.', 'dispatch.php/search/courses', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('74863847eec53a3d4c8264d8de526be8', '74863847eec53a3d4c8264d8de526be8', 'de', 'Mit der Archivsuche können Veranstaltungen gefunden werden, die bereits archiviert wurden.', 'archiv.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('14b77e9e0b7773c92db9e7344a23fcfc', '14b77e9e0b7773c92db9e7344a23fcfc', 'de', 'Mit der Personensuche können NutzerInnen gefunden werden, solange deren Privatsphäre-Einstellung dies nicht verhindert. Die Suche kann auf bestimmte Veranstaltungen oder Einrichtungen begrenzt werden.', 'browse.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('4f9d79fe88e81486b8c1f192d70232d5', '4f9d79fe88e81486b8c1f192d70232d5', 'de', 'Mit der Einrichtungssuche können Einrichtungen über ein freies Suchfeld oder den Einrichtungsbaum gefunden werden.', 'institut_browse.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('014a2106d384c0ca55d9311597029ca0', '014a2106d384c0ca55d9311597029ca0', 'de', 'Mit der Ressourcensuche können universitäre Ressourcen wie Räume, Gebäude etc. gefunden werden.', 'resources.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('60b6caf75d0004dfdb0a1adfd66027ed', '60b6caf75d0004dfdb0a1adfd66027ed', 'de', 'Hier können Dozierende Ankündigungen für ihre Veranstaltungen, Einrichtungen und ihre Profilseite erstellen und anzeigen, wobei die Anzeige gefiltert werden kann.', 'dispatch.php/news/admin_news', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('f3deb7a01205637d71a66e2b90b24cba', 'f3deb7a01205637d71a66e2b90b24cba', 'de', 'Hier können RSS-Feeds, d.h. Nachrichtenströme von externen Internetseiten, auf der Startseite eingebunden werden. Je mehr Feeds eingebunden werden, desto länger dauert das Laden der Startseite.', 'dispatch.php/admin/rss_feeds', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('bc1d6ecab9364cfe2c549d262bfda437', 'bc1d6ecab9364cfe2c549d262bfda437', 'de', 'Die Lernmodulschnittstelle ermöglicht es, Selbstlerneinheiten aus externen Programmen wie ILIAS und LON-CAPA in Stud.IP zur Verfügung zu stellen. Für jedes externe System wird ein eigener Benutzer-Account erstellt oder zugeordnet. Mit den entsprechenden Rechten können eigene Lernmodule erstellt werden.', 'dispatch.php/elearning/my_accounts', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('d1de152db139d8c12552610d2f7999c2', 'd1de152db139d8c12552610d2f7999c2', 'de', 'Mit dem Export können Daten über Veranstaltungen und MitarbeiterInnen in folgende Formate exportiert werden: RTF, TXT, CSV, PDF, HTML und XML.', 'export.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('2c55eab1f52d6f7d1021880836906f5b', '2c55eab1f52d6f7d1021880836906f5b', 'de', 'Hier lassen sich Literaturlisten bearbeiten und in der Veranstaltung sichtbar schalten (mit Klick auf das "Auge").', 'dispatch.php/literature/edit_list.php', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('c8e789a0efb73f00f00dacf565524c73', 'c8e789a0efb73f00f00dacf565524c73', 'en', 'Various display options and notification features can be selected and changed in the general settings.', 'dispatch.php/settings/general', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('f5e59c4fc98e1df7fe29b8e9320853e7', 'f5e59c4fc98e1df7fe29b8e9320853e7', 'en', 'The visibility and searchability for the own profile can be set in the privacy settings.', 'dispatch.php/settings/privacy', '3.1', 0, 0, 1, '', '', 1412942388, 0);
