INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('85c000e33732c5596d198776cb884860', '85c000e33732c5596d198776cb884860', 'en', 'In the standard substitution settings lecturers can stipulate a standard substitution, which can manage and change all courses of the lecturer.', 'dispatch.php/settings/deputies', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('b05b27450e363c38c6b4620b902b3496', 'b05b27450e363c38c6b4620b902b3496', 'en', 'The start page will be displayed after the log-in and can be customised to personal needs by using widgets.', 'dispatch.php/start', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('91d6f451c3ef8d8352a076773b0a19ee', '91d6f451c3ef8d8352a076773b0a19ee', 'en', 'The course page shows all subscribed courses (as a standard only those of the last two semesters), all subscribed study groups as well as all institutions, to which one was allocated. You can customise the display through colour groupings, semester filters, etc. ', 'dispatch.php/my_courses', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0237ea35a203be81e44c979d82ef5ee6', '0237ea35a203be81e44c979d82ef5ee6', 'en', 'Archived courses to which the user is allocated appear here. Contents can no longer be changed, however deposited documents can be downloaded as a zip file.', 'dispatch.php/my_courses/archive', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('d97eff1196f6aed8e94f7c5096ebd2a9', 'd97eff1196f6aed8e94f7c5096ebd2a9', 'en', 'Course-related brief and detailed information, announcements, dates and surveys can be found in the overview.', 'dispatch.php/course/overview', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('357bbf06015b2738aae15837f581a07d', '357bbf06015b2738aae15837f581a07d', 'en', 'More detailed information about the course is displayed, such as e.g. the course number, allocations, lecturers, tutors, etc. In the detailed information it is possible to register for a course under actions.', 'dispatch.php/course/details', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0c055cc6ae418a96ff3afa9db13098df', '0c055cc6ae418a96ff3afa9db13098df', 'en', 'The properties of the course can be subsequently changed with the management functions. The simulation of the students view is possible under actions.', 'dispatch.php/course/management', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('615c1887f0ee080043f133681ebf0def', '615c1887f0ee080043f133681ebf0def', 'en', 'Title, description, lecturer, etc. can be changed in the basic data. The processing can partly be blocked if data are taken over from other systems (e.g. LSF/ UniVZ).', 'dispatch.php/course/basicdata/view', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('abfb5d03de288d02df436f9a8bb96d9d', 'abfb5d03de288d02df436f9a8bb96d9d', 'en', 'The photo of the course, which can help students to distinguish between courses on the "my courses" page, can be changed with the photo-upload-function.', 'dispatch.php/course/avatar/update', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('eec46c5d8ea5523d959a8c334455c2ef', 'eec46c5d8ea5523d959a8c334455c2ef', 'en', 'The course can be allocated to a field of study by using the field of study function. The processing can be blocked if data are taken over from other systems (e.g. LSF/ UniVZ).', 'dispatch.php/course/study_areas/show', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('85c709de75085bd56a739e4e8ac6fcad', '85c709de75085bd56a739e4e8ac6fcad', 'en', 'The semester, date and room details of the course can be changed by using the time/room function. The processing can be blocked if data are taken over from other systems (e.g. LSF/ UniVZ).', 'raumzeit.php', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('4e14c94cda99e2ef6462f7fef06d9c91', '4e14c94cda99e2ef6462f7fef06d9c91', 'en', 'The access to a course can be regulated with access authorisations (enrolment procedure) e.g. by passwords, time control and restriction to participants.', 'dispatch.php/course/admission', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('42060187921376807f90e52fad5f9822', '42060187921376807f90e52fad5f9822', 'en', '(Time-controlled) surveys or individual Multiple-/Single-Choice questions can be set up for courses, study groups or the profile by using the survey and test function.', 'admin_vote.php', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('5475d65b07fdaf5f234bf6eed3d5e4a9', '5475d65b07fdaf5f234bf6eed3d5e4a9', 'en', 'With the evaluation function surveys can be set up with Multiple-Choice, Likert and free text questions for courses, study groups, the own profile or institutions. Public templates of other persons can be used hereby. All future, ongoing and ended evaluations are displayed.', 'admin_evaluation.php', '3.1', 0, 0, 1, '', '', 1412942388, 0);
