INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('3607d6daea679dcd7003e076fdd1660a', '3607d6daea679dcd7003e076fdd1660a', 'en', 'The list of participants shows a list of the participants of this course. Further participants can be added,  removed, downgraded, upgraded or allocated to self-defined groups by lecturers.', 'dispatch.php/course/members', '3.1', 0, 0, 1, '', '', 1412942388, 0);
