INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('b74f8459dce2437463096d56db7c73b9', 9, 'Weitere mögliche Aktionen', 'Hier können Sie alle Neuigkeiten als gelesen markieren, Farbgruppierungen nach Belieben ändern oder\r\nauch die Benachrichtigungen über Aktivitäten in den einzelnen Veranstaltungen anpassen.', 'R', 0, '#layout-sidebar SECTION DIV DIV.sidebar-widget-header :eq(1)', 'dispatch.php/my_courses', '', '', '', 1405932320, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('977f41c5c5239c4e86f04c3df27fae38', 1, 'Willkommen im neuen Stud.IP 3.1', 'Dies ist eine Hilfe-Tour, die die wichtigsten Neuerungen in Stud.IP vorstellt. \r\n\r\nUm auf den nächsten Schritt zu kommen, klicken Sie bitte rechts unten auf "Weiter". Eine Hilfe-Tour kann jederzeit durch einen Klick auf "Beenden" beendet werden.', 'TL', 0, '', 'dispatch.php/start', '', '', '', 1405932373, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('977f41c5c5239c4e86f04c3df27fae38', 2, 'Hilfelasche', 'Auf jeder Seite findet sich die Hilfe von nun an in der sogenannten Hilfelasche. Diese öffnet sich mit einem Klick auf das Fragezeichen. \r\n\r\nHier findet sich kurz und knapp zu jeder Seite die wichtigsten Informationen, Links zur ausführlichen Hilfeseite mit Anleitungsvideos und gegebenenfalls Hilfe-Touren.', 'BR', 0, 'DIV.helpbar-container .helpbar-title', 'dispatch.php/start', '', '', '', 1405932475, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('977f41c5c5239c4e86f04c3df27fae38', 3, 'Gliederung der Startseite', 'Die Startseite im neuen Stud.IP ist standardmäßig so gegliedert wie die alte Version. Neu ist, dass jedes Element ("Widget") individuell entfernt und verschoben werden kann. Darüber hinaus können noch weitere Widgets hinzugefügt werden. Hierzu gibt es eine separate Hilfe-Tour und die Hinweise in der Hilfe.', 'TL', 0, '', 'dispatch.php/start', '', '', '', 1405932516, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('b74f8459dce2437463096d56db7c73b9', 10, 'Studiengruppen und Einrichtungen', 'Es besteht zudem die Möglichkeit auf persönliche Studiengruppen oder Einrichtungen zuzugreifen.', 'R', 0, '#nav_browse_my_institutes A', 'dispatch.php/my_courses', '', '', '', 1405932519, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('977f41c5c5239c4e86f04c3df27fae38', 4, 'Sidebar', 'Auf allen Seiten ist nun links die Sidebar positioniert. Sie enthält Funktionen für die aktuelle Seite.\r\nIm alten Stud.IP gab es mit der Infobox auf der rechten Seite etwas Ähnliches.', 'R', 0, 'SECTION.sidebar', 'dispatch.php/start', '', '', '', 1405932633, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('977f41c5c5239c4e86f04c3df27fae38', 5, 'Änderung der Navigation', 'Häufig ist die Reiternavigation vom alten Stud.IP nun in der Sidebar zu finden. Auf dieser Seite sind das zum Beispiel die Punkte "Neue Nachricht schreiben" und der Gesendet-Ordner.', 'R', 0, 'SECTION.sidebar ', 'dispatch.php/messages/overview', '', '', '', 1405932671, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('977f41c5c5239c4e86f04c3df27fae38', 6, 'Studiengruppen und Einrichtungen', 'Die Einrichtungen, die früher unter den Veranstaltungen standen und die Studiengruppen, die früher zwischen den Veranstaltungen standen, finden sich nun in eigenen Reitern wieder.', 'R', 0, '#nav_browse_my_institutes A SPAN', 'dispatch.php/my_courses', '', '', '', 1405932830, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('977f41c5c5239c4e86f04c3df27fae38', 7, 'Hinweise zu den Anmeldeverfahren', 'Im neuen Stud.IP haben sich die Zugangsberechtigungen und Anmeldeverfahren geändert. Das betrifft sowohl Studierende als auch Dozierende. Hierzu gibt es separate Feature-Touren und die Hinweise in der Hilfe.', 'TL', 0, '', 'dispatch.php/my_courses', '', '', '', 1405932890, 0);
